        //TEST LETTURA SENSIBILE

        [TestMethod]
        public void TestLetturaStringa1()
        {
            string sensibile = BioInformatica.LetturaStringa1();
            string sensibile_aspettata = "";
            Assert.AreEqual(sensibile_aspettata, sensibile);
        }

        //TEST LETTURA RESISTENTE

        [TestMethod]
        public void TestLetturaStringa2()
        {
            string resistente = BioInformatica.LetturaStringa2();
