using Microsoft.VisualStudio.TestTools.UnitTesting;
using ClassBioInfo;

namespace TestBioInfo
{
    [TestClass]
    public class Tests
    {
        //TEST LETTURA SENSIBILE

        [TestMethod]
        public void TestLetturaStringa1()
        {
            string sensibile = BioInformatica.LetturaStringa1();
            string sensibile_aspettata = "";
            Assert.AreEqual(sensibile_aspettata, sensibile);
        }

        //TEST LETTURA RESISTENTE

        [TestMethod]
        public void TestLetturaStringa2()
        {
            string resistente = BioInformatica.LetturaStringa2();
            string resistente_aspettata = "";
            Assert.AreEqual(resistente_aspettata, resistente);
        }

        //TEST LUNGHEZZA SENSIBILE

        [TestMethod]
        public void TestLunghezzaStringa1()
        {
            long lunghezzasensibile = BioInformatica.LunghezzaStringa1();
            long lunghezzaaspettata = 3901;
            Assert.AreEqual(lunghezzaaspettata, lunghezzasensibile);
        }

        //TEST LUNGHEZZA RESISTENTE

        [TestMethod]
        public void TestLunghezzaStringa2()
        {
            long lunghezzaresistente = BioInformatica.LunghezzaStringa1();
            long lunghezzaaspettata = 3901;
            Assert.AreEqual(lunghezzaaspettata, lunghezzaresistente);
        }

        [DataTestMethod]
        [DataRow("sensibile.txt", "resistente.txt,", 3901, 3901)]
        public void DataTestLunghezza(string filesensibile, string fileresistente, int lunghezzasensibile, int lunghezzaresistente)
        {
            long lungsensibile = BioInformatica.LunghezzaStringa1();
            long lungresistente = BioInformatica.LunghezzaStringa2();
            Assert.AreEqual(lunghezzasensibile, lungsensibile);
            Assert.AreEqual(lungresistente, lungresistente);
        }

        //TEST POSIZIONE DELLA PRIMA DIFFERENZA

        [TestMethod]
        public void TestPosizione()
        {
            long posizione = BioInformatica.Prima_differenza();
            long posizione_aspettata = 1603;
            Assert.AreEqual(posizione_aspettata, posizione);
        }

        //PRIMA PORZIONE DI SEGUENZA IDENTICA

        [TestMethod]
        public void TestParteUguale()
        {
            string parte_uguale = BioInformatica.Parte_Uguale();
            string parte_uguale_aspettata = "";
            Assert.AreEqual(parte_uguale_aspettata, parte_uguale);
        }

        //TEST NUMERO CARATTERI DIFFERENTI

        [TestMethod]
        public void TestNumeroCaratteriDifferenti()
        {
            int numero_caratteri_differenti = BioInformatica.numero_caratteri_differenti();
            int numero_caratteri_differenti_aspettati = 1;
            Assert.AreEqual(numero_caratteri_differenti_aspettati, numero_caratteri_differenti);
        }
    }
}
